// Feature detection with environment check
function initializeAnalytics() {
  if (!isBrowser()) {
    console.log('Analytics not available in server environment');
    return;
  }

  // Browser-only analytics initialization
  if (window.gtag) {
    gtag('config', 'GA_MEASUREMENT_ID');
  }
}
